import React, { Component, useState } from "react";
import PropTypes from "prop-types";
import "./AutoCompleteSearchInput.css";
import SearchIcon from "../../assets/icons/search.svg";
import PlusRedIcon from "../../assets/icons/plus_red.svg";

class AutoCompleteSearchInput extends Component {
  static propTypes = {
    suggestions: PropTypes.instanceOf(Array),
  };

  static defaultProps = {
    suggestions: [],
  };

  constructor(props) {
    super(props);

    this.state = {
      // The active selection's index
      activeSuggestion: 0,
      // The suggestions that match the user's input
      filteredSuggestions: [],
      // Whether or not the suggestion list is shown
      showSuggestions: false,
      // What the user has entered
      userInput: "",
      focused: false,
    };
  }

  onChange = (e) => {
    const { suggestions } = this.props;
    const userInput = e.currentTarget.value;
    this.setState({
      focused: true,
    });

    // Filter our suggestions that don't contain the user's input
    const filteredSuggestions = suggestions.filter(
      (suggestion) =>
        suggestion.toLowerCase().indexOf(userInput.toLowerCase()) > -1
    );

    this.setState({
      activeSuggestion: 0,
      filteredSuggestions,
      showSuggestions: true,
      userInput: e.currentTarget.value,
    });
  };

  onClick = (e) => {
    this.setState({
      activeSuggestion: 0,
      filteredSuggestions: [],
      showSuggestions: false,
      userInput: e.currentTarget.innerText,
    });
  };

  onKeyDown = (e) => {
    const { activeSuggestion, filteredSuggestions } = this.state;

    // User pressed the enter key
    if (e.keyCode === 13) {
      this.setState({
        activeSuggestion: 0,
        showSuggestions: false,
        userInput: filteredSuggestions[activeSuggestion],
      });
    }
    // User pressed the up arrow
    else if (e.keyCode === 38) {
      if (activeSuggestion === 0) {
        return;
      }

      this.setState({ activeSuggestion: activeSuggestion - 1 });
    }
    // User pressed the down arrow
    else if (e.keyCode === 40) {
      if (activeSuggestion - 1 === filteredSuggestions.length) {
        return;
      }

      this.setState({ activeSuggestion: activeSuggestion + 1 });
    }
  };

  render() {
    const {
      onChange,
      onClick,
      onKeyDown,
      state: {
        activeSuggestion,
        filteredSuggestions,
        showSuggestions,
        userInput,
      },
    } = this;

    let suggestionsListComponent;

    if (showSuggestions && userInput) {
      if (filteredSuggestions.length) {
        suggestionsListComponent = (
          <ul class="suggestions">
            {filteredSuggestions.map((suggestion, index) => {
              let className;

              // Flag the active suggestion with a class
              if (index === activeSuggestion) {
                className = "suggestion-active";
              }

              return (
                <li className={className} key={suggestion} onClick={onClick}>
                  {suggestion}
                </li>
              );
            })}
          </ul>
        );
      } else {
        suggestionsListComponent = (
          <div className="no-suggestions">
            <em>Oops! Sorry No Match !</em>
          </div>
        );
      }
    }

    const searchDivClass = this.state.focused
      ? "search-div-transparent"
      : "search-div";

    const onFocus = () => {
      // this.props.setIsActive(true);
      this.setState({ focused: true });
    };
    const onBlur = () => {
      // this.props.setIsActive(false);
      this.setState({ focused: false });
    };

    return (
      <div className={searchDivClass}>
        <div className="search-wrapper">
          <div className="icon-input-div">
            <img src={SearchIcon} alt="" />
            <input
              type="text"
              onChange={onChange}
              onKeyDown={onKeyDown}
              value={userInput}
              onBlur={onBlur}
            />
          </div>
        </div>
        {this.state.focused && (
          <div
            className={
              this.state.focused
                ? "suggestion-container show"
                : "suggestion-container hide"
            }
          >
            {suggestionsListComponent}
            <div className="add-btn-div">
              <img src={PlusRedIcon} alt="" />
              <button className="add-new" onClick={this.props.modalToggler}>
                Add New
              </button>
            </div>
          </div>
        )}
      </div>
    );
  }
}

export default AutoCompleteSearchInput;
