import React, { useState, useEffect } from "react";
import PrimaryButtonWithIcon from "../../components/PrimaryButtonWithIcon/PrimaryButtonWithIcon";
import { SearchInput } from "../../components/SearchInput";
import PlusIcon from "../../assets/icons/plus.svg";
import "./Projects.css";
import ProjectCard from "../../components/ProjectCard/ProjectCard";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProjects, createProject } from "../../actions/projects";

import { Modal } from "../../components/Modal";
import { TextInput } from "../../components/TextInput";
import { PrimaryButton } from "../../components/PrimaryButton";
import Multiselect from "multiselect-react-dropdown";

function Projects(props) {
  const initialState = {
    inputSearch: "",
  };

  const initialStateModalForm = {
    inputProjectName: "",
    inputProjectDescription: "",
    inputProjectSkills: [],
  };

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [state, setState] = useState(initialState);
  const [stateModalForm, setStateModalForm] = useState(initialStateModalForm);
  const [isOpen, setOpen] = useState(false);

  const [data, setData] = useState({});
  const [userSkills, setUserSkills] = useState([]);

  const sample = ["HTML", "CSS", "JavaScript", "React", "NodeJS", "Express"];
  const projects = useSelector((state) => state.projects);
  const skills = useSelector((state) => state.skills);
  const dispatch = useDispatch();

  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProjects());
  }, []);

  useEffect(() => {
    if (skills) {
      setUserSkills(skills);
    }
  }, [skills]);

  const handleChange = (e) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };

  function handleChangeModalForm(e) {
    const value = e.target.value;
    setStateModalForm({
      ...stateModalForm,
      [e.target.name]: value,
    });
  }

  useEffect(() => {
    if (projects.length > 0) {
      setData(projects);
    }
  }, [projects]);

  let projectsContent = null;
  if (projects.length > 0) {
    console.log(projects);
    projectsContent = projects.map((project) => {
      return (
        <ProjectCard
          key={project.id}
          projectName={project.name}
          projectDescription={project.description}
          projectSkills={project.skills}
        />
      );
    });
  } else {
    projectsContent = <div>Loading ...</div>;
  }

  if(userSkills.length > 0) {

  }
  return (
    <div className="projects">
      <header className="projects-header">
        <SearchInput
          name="inputSearch"
          value={state.inputSearch}
          placeholder="Search for projects"
          onChange={handleChange}
        />
        <PrimaryButtonWithIcon icon={PlusIcon} onClick={() => setOpen(!isOpen)}>
          Add Project
        </PrimaryButtonWithIcon>
      </header>
      <section className="projects-content">{projectsContent}</section>
      <div className="">
        {isOpen && projects && (
          <>
            <div className="modal-container">
              <Modal heading={"Add Project"} isOpen={isOpen} setOpen={setOpen}>
                <form action="">
                  <TextInput
                    type="text"
                    label="Project Name"
                    name="inputProjectName"
                    value={stateModalForm.inputProjectName}
                    placeholder="Enter your project name here"
                    onChange={handleChangeModalForm}
                  />
                  <TextInput
                    type="text"
                    label="Project Description"
                    name="inputProjectDescription"
                    value={stateModalForm.inputProjectDescription}
                    placeholder="Enter your project description here"
                    onChange={handleChangeModalForm}
                  />
                  <Multiselect
                    options={sample} // Options to display in the dropdown
                    selectedValues={""} // Preselected value to persist in dropdown
                    onSelect={() => {}} // Function will trigger on select event
                    onRemove={() => {}} // Function will trigger on remove event
                    isObject={false}
                    placeholder="Select your project skills"
                  />

                  {stateModalForm.inputProjectName.length > 0 &&
                    stateModalForm.inputProjectDescription.length > 0 && (
                      <PrimaryButton onClick={() => {}}> Save </PrimaryButton>
                    )}
                </form>
              </Modal>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Projects;
