import React, { useEffect, useRef, useState } from "react";
import { TextArea } from "../../components/TextArea";
import { TextInput } from "../../components/TextInput";
import { TextInputRef } from "../../components/TextInputRef";
import { TextAreaRef } from "../../components/TextAreaRef";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Profile.css";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProfile, createProfile } from "../../actions/profile";
import { Modal } from "../../components/Modal";
import ProfileHeader from "./components/ProfileHeader";
import ProfileDetails from "./components/ProfileDetails";
import EditProfileModal from "./components/EditProfileModal";
import ProfileContent from "./components/ProfileContent";
import ProfileContainer from "./components/ProfileContainer";

function Profile(props) {
  const initialStateModalForm = {
    inputFirstName: "",
    inputLastName: "",
    inputLocation: "",
    inputContact: "",
    inputAbout: "",
  };
  const [stateModalForm, setStateModalForm] = useState(initialStateModalForm);

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [data, setData] = useState({});
  const [isOpen, setOpen] = useState(false);

  const profile = useSelector((state) => state.profile);
  const dispatch = useDispatch();
  // if (!currentUser) {
  //   navigate("/login");
  // }

  const inputRef = useRef({});

  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProfile());
  }, []);
  // Handle input change for each input

  function handleChangeModalForm(e) {
    const value = e.target.value;
    setStateModalForm({
      ...stateModalForm,
      [e.target.name]: value,
    });
  }

  function handleEdit(e) {
    e.preventDefault();
    dispatch(
      createProfile({
        firstName: stateModalForm.inputFirstName,
        lastName: stateModalForm.inputLastName,
        location: stateModalForm.inputLocation,
        contactNo: stateModalForm.inputContact,
        aboutMe: stateModalForm.inputAbout,
      })
    );
    setOpen(false);
    setTimeout(() => {
      dispatch(getProfile());
    }, 1000);
  }

  useEffect(() => {
    setStateModalForm({
      inputFirstName: profile.firstName,
      inputLastName: profile.lastName,
      inputLocation: profile.location,
      inputContact: profile.contactNo,
      inputAbout: profile.aboutMe,
    });
  }, [profile]);

  function handleEditProfile(e) {
    e.preventDefault();
    dispatch(
      createProfile({
        firstName: inputRef.current.inputFirstName.value,
        lastName: inputRef.current.inputLastName.value,
        location: inputRef.current.inputLocation.value,
        contactNo: inputRef.current.inputContact.value,
        aboutMe: inputRef.current.inputAbout.value,
      })
    );
    setOpen(false);
    setTimeout(() => {
      dispatch(getProfile());
    }, 1000);
  }

  if (currentUser && profile) {
    profile.email = currentUser.email;
  }
  return (
    <ProfileContainer>
      <ProfileHeader profile={profile} modalToggler={() => setOpen(!isOpen)} />
      <ProfileContent>
        <ProfileDetails profile={profile} />
        <EditProfileModal
          profile={profile}
          isOpen={isOpen}
          setOpen={setOpen}
          inputRef={inputRef}
          handleEditProfile={handleEditProfile}
        />
      </ProfileContent>
      </ProfileContainer>
  );
}

export default Profile;
