import React, { useState,useEffect } from "react";
import PrimaryButtonWithIcon from "../../components/PrimaryButtonWithIcon/PrimaryButtonWithIcon";
import { SearchInput } from "../../components/SearchInput";
import PlusIcon from "../../assets/icons/plus.svg";
import "./Projects.css";
import ProjectCard from "../../components/ProjectCard/ProjectCard";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProjects, createProject } from "../../actions/projects";

import { Modal } from "../../components/Modal";
import { TextInput } from "../../components/TextInput";


function Projects(props) {
  const initialState = {
    inputSearch: "",
  };

  const initialStateModalForm = {
    inputProjectName: "",
    inputProjectDescription: "",
    inputProjectSkills: [],
  };

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [state, setState] = useState(initialState);
  const [stateModalForm, setStateModalForm] = useState(initialStateModalForm);
  const [isOpen, setOpen] = useState(false);

  const [data, setData] = useState({});

  const projects = useSelector((state) => state.projects);
  const dispatch = useDispatch();

  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProjects());
  }, []);

  const handleChange = (e) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };

  function handleChangeModalForm(e) {
    const value = e.target.value;
    setStateModalForm({
      ...stateModalForm,
      [e.target.name]: value,
    });
  }

  useEffect(() => {
    if (projects) {
      setData(skills);
    }
  }, [skills]);
  return (
    <div className="projects">
      <header className="projects-header">
        <SearchInput
          name="inputSearch"
          value={state.inputSearch}
          placeholder="Search for projects"
          onChange={handleChange}
        />
        <PrimaryButtonWithIcon icon={PlusIcon} onClick={() => {}}>
          Add Project
        </PrimaryButtonWithIcon>
      </header>
      <section className="projects-content">
        <ProjectCard projectName={"Project 1"} projectDescription={'Description 1'} projectSkills={['C','Java','Python']} />
        <ProjectCard projectName={"Project 2"} projectDescription={'Description 2'} projectSkills={['React JS','Javascript','Node JS']} />
      </section>
    </div>
  );
}

export default Projects;
