import React, { useState, useEffect, useRef } from "react";
import { SearchInput } from "../../components/SearchInput";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Skills.css";
import { SkillCard } from "../../components/SkillCard";
import PlusIcon from "../../assets/icons/plus.svg";
import PrimaryButtonWithIcon from "../../components/PrimaryButtonWithIcon/PrimaryButtonWithIcon";
import ReactStars from "react-rating-stars-component";
import EmptyStarIcon from "../../assets/icons/star_outline.svg";
import FullStarIcon from "../../assets/icons/star_filled.svg";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getSkills, createSkill } from "../../actions/skills";

import { Modal } from "../../components/Modal";
import { TextInputRef } from "../../components/TextInputRef";
import SkillsList from "./SkillsList";

function Skills(props) {
  const initialState = {
    inputSearch: "",
  };

  const initialStateModalForm = {
    inputSkillLevel: 0,
  };

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [state, setState] = useState(initialState);
  const [stateModalForm, setStateModalForm] = useState(initialStateModalForm);
  const [isOpen, setOpen] = useState(false);

  const [data, setData] = useState({});

  const skills = useSelector((state) => state.skills);
  const dispatch = useDispatch();

  const inputRef = useRef({});

  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getSkills());
  }, []);

  const handleChange = (e) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };

  // function handleChangeModalForm(e) {
  //   const value = e.target.value;
  //   setStateModalForm({
  //     ...stateModalForm,
  //     [e.target.name]: value,
  //   });
  // }

  const levelChanged = (newLevel) => {
    setStateModalForm({
      ...stateModalForm,
      inputSkillLevel: newLevel,
    });
  };

  function handleSave(e) {
    e.preventDefault();
    dispatch(
      createSkill({
        skill: {
          name: inputRef.current.inputSkillName.value,
          level: stateModalForm.inputSkillLevel,
        },
      })
    );
    setStateModalForm({
      inputSkillLevel: 0,
    });
    setTimeout(() => {
      dispatch(getSkills());
    }, 1000);
    setOpen(false);
  }

  useEffect(() => {
    if (skills) {
      setData(skills);
    }
  }, [skills]);

  let skillsContent = null;
  if (skills.length > 0) {
    console.log(skills);
    // skillsContent = skills.map((skill) => {
    //   return (
    //     <SkillCard key={skill.id} skillName={skill.name} level={skill.level} />
    //   );
    // });
    skillsContent = <SkillsList skills={skills} />;
  } else {
    skillsContent = <div>Loading ...</div>;
  }

  return (
    <div className="skills">
      <header className="skills-header">
        <SearchInput
          name="inputSearch"
          value={state.inputSearch}
          placeholder="Search for skills"
          onChange={handleChange}
        />
        <PrimaryButtonWithIcon icon={PlusIcon} onClick={() => setOpen(!isOpen)}>
          Add Skill
        </PrimaryButtonWithIcon>
      </header>
      <section className="skills-content">
        {skillsContent}
        {/* <SkillCard skillName={"C"} rating={3} />
        <SkillCard skillName={"C"} rating={3} />
        <SkillCard skillName={"C"} rating={3} /> */}
      </section>
      <div className="">
        {isOpen && skills && (
          <>
            <div className="modal-container">
              <Modal heading={"Add Skill"} isOpen={isOpen} setOpen={setOpen}>
                <form action="">
                  <TextInputRef
                    type="text"
                    label="Skill Name"
                    name="inputSkillName"
                    value={""}
                    placeholder="Enter your skill here"
                    ref={inputRef}
                  />
                  <label htmlFor="" className="skill-level-label">
                    Skill Level
                  </label>
                  <ReactStars
                    count={5}
                    onChange={levelChanged}
                    size={36}
                    emptyIcon={<img src={EmptyStarIcon} alt="" />}
                    filledIcon={<img src={FullStarIcon} alt="" />}
                    activeColor="#D83A52"

                  />
                  {/* {stateModalForm.inputSkillLevel > 0 &&
                    stateModalForm.inputSkillName.length > 0 && (
                      <PrimaryButton onClick={handleSave}> Save </PrimaryButton>
                    )} */}
                  <PrimaryButton onClick={handleSave}> Save </PrimaryButton>
                </form>
              </Modal>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Skills;
