import React, { useState, useEffect } from "react";
import { TextInputAuth } from "../../components/TextInputAuth";
import "./SignUp.css";
import EmailIcon from "../../assets/icons/email.svg";
import PasswordIcon from "../../assets/icons/password.svg";
import { PrimaryButton } from "../../components/PrimaryButton";
import useApi from "../../hooks/useApi";
import userApi from "../../api/user";

function SignUp() {
  const registerUserApi = useApi(userApi.registerUser);

  useEffect(() => {
    registerUserApi.request()
  }, []);

  const initialState = {
    inputEmail: "",
    inputPassword: "",
    inputConfirmPassword: "",
  };
  const [state, setState] = useState(initialState);

  // Handle input change for each input
  function handleChange(e) {
    const value = e.target.value;
    setState({
      ...state,
      [e.target.name]: value,
    });
  }

  // Handle Signup button click
  function handleSignUp(e) {
    e.preventDefault();
    console.log(state);
  }

  return (
    <section className="sign-up-container">
      <h1>Sign Up</h1>
      <form className="sign-up-form">
        <TextInputAuth
          icon={EmailIcon}
          type={"email"}
          label={"Email address"}
          name={"inputEmail"}
          value={state.inputEmail}
          placeholder={"Enter your email address here"}
          onChange={handleChange}
          required={true}
        />
        <TextInputAuth
          icon={PasswordIcon}
          type={"password"}
          label={"Password"}
          name={"inputPassword"}
          value={state.inputPassword}
          placeholder={"Enter your password here"}
          onChange={handleChange}
        />
        <TextInputAuth
          icon={PasswordIcon}
          type={"password"}
          label={"Password"}
          name={"inputConfirmPassword"}
          value={state.inputConfirmPassword}
          placeholder={"Re-enter your password here"}
          onChange={handleChange}
        />
        <PrimaryButton onClick={handleSignUp}>Sign Up</PrimaryButton>
      </form>
    </section>
  );
}

export default SignUp;
