import React, { useEffect, useRef, useState } from "react";
import { TextArea } from "../../components/TextArea";
import { TextInput } from "../../components/TextInput";
import { TextInputRef } from "../../components/TextInputRef";
import { TextAreaRef } from "../../components/TextAreaRef";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Profile.css";

import EditIcon from "../../assets/icons/edit.svg";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProfile, createProfile } from "../../actions/profile";
import { Modal } from "../../components/Modal";

function Profile(props) {
  const initialStateModalForm = {
    inputFirstName: "",
    inputLastName: "",
    inputLocation: "",
    inputContact: "",
    inputAbout: "",
  };
  const [stateModalForm, setStateModalForm] = useState(initialStateModalForm);

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [data, setData] = useState({});
  const [isOpen, setOpen] = useState(false);

  const profile = useSelector((state) => state.profile);
  const dispatch = useDispatch();
  // if (!currentUser) {
  //   navigate("/login");
  // }

  const inputRef = useRef({});

  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProfile());
  }, []);
  // Handle input change for each input

  function handleChangeModalForm(e) {
    const value = e.target.value;
    setStateModalForm({
      ...stateModalForm,
      [e.target.name]: value,
    });
  }

  function handleEdit(e) {
    e.preventDefault();
    dispatch(
      createProfile({
        firstName: stateModalForm.inputFirstName,
        lastName: stateModalForm.inputLastName,
        location: stateModalForm.inputLocation,
        contactNo: stateModalForm.inputContact,
        aboutMe: stateModalForm.inputAbout,
      })
    );
    setOpen(false);
    setTimeout(() => {
      dispatch(getProfile());
    }, 1000);
  }

  useEffect(() => {
    setStateModalForm({
      inputFirstName: profile.firstName,
      inputLastName: profile.lastName,
      inputLocation: profile.location,
      inputContact: profile.contactNo,
      inputAbout: profile.aboutMe,
    });
  }, [profile]);

  function handleEditProfile(e) {
    e.preventDefault();
    dispatch(
      createProfile({
        firstName: inputRef.current.firstName.value,
        lastName: stateModalForm.inputLastName,
        location: stateModalForm.inputLocation,
        contactNo: stateModalForm.inputContact,
        aboutMe: stateModalForm.inputAbout,
      })
    );
    setOpen(false);
    setTimeout(() => {
      dispatch(getProfile());
    }, 1000);
  }
  return (
    <div className="profile">
      <header className="profile-header">
        <div className="info-left">
          <span className="avatar">JR</span>
          <h3 className="profile-name-location">
            <span className="profile-name">
              {profile.firstName} {profile.lastName}
            </span>
            <span className="profile-location">{profile.location}</span>
          </h3>
        </div>
        <button className="edit-btn" onClick={() => setOpen(!isOpen)}>
          <img src={EditIcon} alt="close" />
        </button>
      </header>
      <form className="profile-main">
        <TextInput
          type="email"
          label="Email address"
          name="emailInput"
          value={currentUser.email}
          placeholder="Enter your email here"
          onChange={() => {}}
        />
        <TextInput
          type="text"
          label="Contact"
          name="inputContactR"
          value={profile.contactNo}
          placeholder="Enter your contact here"
          onChange={() => {}}
        />
        <TextArea
          label="About Me"
          name="inputAbout"
          value={profile.aboutMe}
          placeholder="Tell us about yourself"
          onChange={() => {}}
        />
      </form>
      <div className="">
        {isOpen && (
          <>
            <div className="modal-container">
              <Modal heading={"Edit Profile"} isOpen={isOpen} setOpen={setOpen}>
                <form action="">
                  <TextInputRef
                    type="text"
                    label="First Name"
                    name="inputFirstName"
                    value={stateModalForm.inputFirstName}
                    placeholder="Enter your first here"
                    onChange={handleChangeModalForm}
                    ref={inputRef}
                  />
                  <TextInputRef
                    type="text"
                    label="Last Name"
                    name="inputLastName"
                    value={stateModalForm.inputLastName}
                    placeholder="Enter your last here"
                    onChange={handleChangeModalForm}
                    ref={inputRef}
                  />
                  <TextInputRef
                    type="text"
                    label="Location"
                    name="inputLocation"
                    value={stateModalForm.inputLocation}
                    placeholder="Enter your location here"
                    onChange={handleChangeModalForm}
                    ref={inputRef}
                  />

                  <TextInputRef
                    type="text"
                    label="Contact"
                    name="inputContact"
                    value={stateModalForm.inputContact}
                    placeholder="Enter your contact here"
                    onChange={handleChangeModalForm}
                    ref={inputRef}
                  />
                  {/* <TextInputRef
                    label="About Me"
                    name="inputAbout"
                    value={stateModalForm.inputAbout}
                    placeholder="Tell us about yourself"
                    onChange={handleChangeModalForm}
                    ref={inputRef}
                  /> */}
                  <TextAreaRef
                    label="About Me"
                    name="inputAbout"
                    value={stateModalForm.inputAbout}
                    placeholder="Tell us about yourself"
                    onChange={handleChangeModalForm}
                    ref={inputRef}
                  />
                  <PrimaryButton onClick={handleEdit}> Save </PrimaryButton>
                </form>
              </Modal>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Profile;
