import React, { useEffect, useState } from "react";
import { TextArea } from "../../components/TextArea";
import { TextInput } from "../../components/TextInput";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Profile.css";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProfile, createProfile } from "../../actions/profile";
import styled from "styled-components";

function Profile(props) {
  const initialState = {
    inputContact: "",
    inputAbout: "",
  };
  const [state, setState] = useState(initialState);

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [data, setData] = useState({});
  const [isOpen, setOpen] = useState(false);

  const profile = useSelector((state) => state.profile);
  const dispatch = useDispatch();
  // if (!currentUser) {
  //   navigate("/login");
  // }
  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProfile());
  }, []);
  // Handle input change for each input
  function handleChange(e) {
    const value = e.target.value;
    setState({
      ...state,
      [e.target.name]: value,
    });
  }

  const Container = styled.div``;

  const ModalContainer = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: rgba(0, 0, 0, 0.5);
  `;

  const Modal = styled.div`
    background: #fff;
    position: absolute;
  left: 65%;
  top: 50%;
  transform: translate(-50%, -50%);
    border: 1px solid #000;
    padding: 20px;
    min-height: 200px;
  `;
  return (
    <div className="profile">
      <header className="profile-header">
        <span className="avatar">JR</span>
        <h3 className="profile-name-location">
          <span className="profile-name">
            {profile.firstName} {profile.lastName}
          </span>
          <span className="profile-location">{profile.location}</span>
        </h3>
      </header>
      <form className="profile-main">
        <TextInput
          type="email"
          label="Email address"
          name="emailInput"
          value={currentUser.email}
          placeholder="Enter your email here"
          onChange={() => {}}
        />
        <TextInput
          type="text"
          label="Contact"
          name="inputContact"
          value={profile.contactNo || state.inputContact}
          placeholder="Enter your contact here"
          onChange={handleChange}
        />
        <TextArea
          label="About Me"
          name="inputAbout"
          value={profile.aboutMe || state.inputAbout}
          placeholder="Tell us about yourself"
          onChange={handleChange}
        />

        <PrimaryButton onClick={() => {}}> Save </PrimaryButton>
      </form>
      <Container>
        <div>When modal is opened, this background want to be dark. </div>

        <button onClick={() => setOpen(!isOpen)}>Show Modal</button>
        {isOpen && (
          <>
            <ModalContainer>
              <Modal>I am a modal, I want to be special.</Modal>
            </ModalContainer>
          </>
        )}
      </Container>
    </div>
  );
}

export default Profile;
