import React from 'react'
import './TextArea.css'

function TextArea({label, name, value, placeholder, onChange}) {
  return (
    <div className='textarea-input-wrapper-main'>
        <div className="label-container-textarea">
        <label htmlFor={name}>{label}</label>
        </div>
        <textarea className='textarea-main' name={name} value={value} placeholder={placeholder} onChange={onChange}   />
    </div>
  )
}

export default TextArea
