    e.preventDefault();
    dispatch(
      createSkill({
        skill: {
          name: inputRef.current.inputSkillName.value,
          level: stateModalForm.inputSkillLevel,
        },
      })
    );
    setStateModalForm({
      inputSkillLevel: 0,
    });
    setTimeout(() => {
      dispatch(getSkills());
    }, 1000);
    setOpen(false);
