import React from 'react'
import './TextAreaRef.css'

function TextAreaRef({label, name, value, placeholder, onChange}, ref) {
  return (
    <div className='textarea-input-wrapper-main'>
        <div className="label-container-textarea">
        <label htmlFor={name}>{label}</label>
        </div>
        <textarea className='textarea-main' name={name} value={value} placeholder={placeholder} onChange={onChange} rows="5"  />
    </div>
  )
}

export default TextAreaRef;
