import React, { useEffect, useState } from "react";
import { TextArea } from "../../components/TextArea";
import { TextInput } from "../../components/TextInput";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Profile.css";
import CloseIcon from "../../assets/icons/close.svg";
import EditIcon from "../../assets/icons/edit.svg";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProfile, createProfile } from "../../actions/profile";
import { Modal } from "../../components/Modal";
import styled from "styled-components";
import AddProfileModal from "./AddProfileModal";

function Profile(props) {
  const initialState = {
    inputContact: "",
    inputAbout: "",
  };
  const initialStateModalForm = {
    inputFirstName: "",
    inputLastName: "",
    inputLocation: "",
    inputContact: "",
    inputAbout: "",
  };
  const [state, setState] = useState(initialState);
  const [stateModalForm, setStateModalForm] = useState(initialStateModalForm);

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const [data, setData] = useState({});
  const [isOpen, setOpen] = useState(false);

  const profile = useSelector((state) => state.profile);
  const dispatch = useDispatch();
  // if (!currentUser) {
  //   navigate("/login");
  // }
  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProfile());
  }, []);
  // Handle input change for each input
  function handleChange(e) {
    const value = e.target.value;
    setState({
      ...state,
      [e.target.name]: value,
    });
  }

  function handleChangeModalForm(e) {
    const value = e.target.value;
    setStateModalForm({
      ...stateModalForm,
      [e.target.name]: value,
    });
  }

  const Container = styled.div``;

  const ModalContainer = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: rgba(0, 0, 0, 0.5);
  `;

  const ModalDiv = styled.div`
    background: #fff;
    position: absolute;
    left: 60%;
    top: 50%;
    transform: translate(-50%, -50%);
    border-radius: 16px;
    padding: 48px;
    min-height: fit-content;
    outline: none;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
  `;
  return (
    <div className="profile">
      <header className="profile-header">
        <div className="info-left">
          <span className="avatar">JR</span>
          <h3 className="profile-name-location">
            <span className="profile-name">
              {profile.firstName} {profile.lastName}
            </span>
            <span className="profile-location">{profile.location}</span>
          </h3>
        </div>
        <button className="edit-btn" onClick={() => setOpen(!isOpen)}>
          <img src={EditIcon} alt="close" />
        </button>
      </header>
      <form className="profile-main">
        <TextInput
          type="email"
          label="Email address"
          name="emailInput"
          value={currentUser.email}
          placeholder="Enter your email here"
          onChange={() => {}}
        />
        <TextInput
          type="text"
          label="Contact"
          name="inputContact"
          value={profile.contactNo || state.inputContact}
          placeholder="Enter your contact here"
          onChange={handleChange}
        />
        <TextArea
          label="About Me"
          name="inputAbout"
          value={profile.aboutMe || state.inputAbout}
          placeholder="Tell us about yourself"
          onChange={handleChange}
        />

        <PrimaryButton onClick={() => {}}> Save </PrimaryButton>
      </form>
      <Container>
        <button onClick={() => setOpen(!isOpen)}>Show Modal</button>
        {isOpen && (
          <>
            <ModalContainer>
              <Modal heading={"Edit Profile"} isOpen={isOpen} setOpen={setOpen}>
                <AddProfileModal initialState={ini} />
              </Modal>
            </ModalContainer>
          </>
        )}
      </Container>
    </div>
  );
}

export default Profile;
