import React, { useEffect, useState } from "react";
import { TextArea } from "../../components/TextArea";
import { TextInput } from "../../components/TextInput";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Profile.css";

import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProfile, createProfile } from "../../actions/profile";

function Profile(props) {
  const initialState = {
    inputContact: "",
    inputAbout: "",
  };
  const [state, setState] = useState(initialState);

  let navigate = useNavigate();

  const { user: currentUser } = useSelector((state) => state.auth);

  const profile = useSelector((state) => state.profile);
  const dispatch = useDispatch();
  // if (!currentUser) {
  //   navigate("/login");
  // }
  useEffect(() => {
    if (!currentUser) {
      navigate("/login");
    }
  }, [currentUser]);

  useEffect(() => {
    dispatch(getProfile());
  }, []);
  // Handle input change for each input
  function handleChange(e) {
    const value = e.target.value;
    setState({
      ...state,
      [e.target.name]: value,
    });
  }

  const getInitials = (firstName, lastName) => {
    let initials =
      firstName.charAt(0).toUpperCase() + lastName.charAt(0).toUpperCase();
    return initials;
  };
  return (
    <div className="profile">
      <header className="profile-header">
        <span className="avatar">
          {getInitials(profile)}
        </span>
        <h3 className="profile-name-location">
          <span className="profile-name">
            {profile.firstName} {profile.lastName}
          </span>
          <span className="profile-location">San Francisco, CA</span>
        </h3>
      </header>
      <form className="profile-main">
        <TextInput
          type="email"
          label="Email address"
          name="emailInput"
          value=""
          placeholder="Enter your email here"
          onChange={() => {}}
        />
        <TextInput
          type="text"
          label="Contact"
          name="inputContact"
          value={state.inputContact}
          placeholder="Enter your contact here"
          onChange={handleChange}
        />
        <TextArea
          label="About Me"
          name="inputAbout"
          value={state.inputAbout}
          placeholder="Tell us about yourself"
          onChange={handleChange}
        />

        <PrimaryButton onClick={() => {}}> Save </PrimaryButton>
      </form>
    </div>
  );
}

export default Profile;
