import React, { useEffect, createRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getSkills } from "../../actions/skills";
import { getProjects } from "../../actions/projects";
import { getProfile } from "../../actions/profile";
import SkillsList from "../Skills/SkillsList";

import "./Report.css";
import ProjectsList from "../Projects/ProjectsList";
import { TextArea } from "../../components/TextArea";
import { PDFExport } from "@progress/kendo-react-pdf";
import ReportHeader from "./ReportHeader";
import ReportContainer from "./ReportContainer";
import ProfileSection from "./ProfileSection";

function Report(props) {
  const skills = useSelector((state) => state.skills);
  const projects = useSelector((state) => state.projects);
  const profile = useSelector((state) => state.profile);
  const { user: currentUser } = useSelector((state) => state.auth);

  const dispatch = useDispatch();

  const reportRef = React.createRef();
  const pdfExportComponent = createRef();

  useEffect(() => {
    dispatch(getProfile());
    dispatch(getSkills());
    dispatch(getProjects());
  }, []);

  let skillsContent = null;
  if (skills.length > 0) {
    console.log(skills);

    skillsContent = <SkillsList skills={skills} />;
  } else {
    skillsContent = <div>Loading ...</div>;
  }
  let projectsContent = null;
  if (projects.length > 0) {
    projectsContent = <ProjectsList projects={projects} />;
  } else {
    projectsContent = <div>Loading ...</div>;
  }

  const downloadReport = () => {
    if (pdfExportComponent.current) {
      pdfExportComponent.current.save();
    }
  };

  return (
    <ReportContainer>
      <ReportHeader
        downloadReport={downloadReport}
      />
      <PDFExport
        ref={pdfExportComponent}
        scale={0.8}
        paperSize="A4"
        margin="1cm"
      >
        <div className="report-content">
          {/* <header className="profile-header">
            <div className="info-left">
              <span className="avatar">JR</span>
              <h3 className="profile-name-location">
                <span className="profile-name">
                  {profile.firstName} {profile.lastName}
                </span>
                <span className="profile-location">{profile.location}</span>
                <div className="profile-email-contact-div">
                  <span className="profile-email">{currentUser.email}</span>
                  <span className="profile-contact">{profile.contactNo}</span>
                </div>
              </h3>
            </div>
          </header> */}
          <ProfileSection profile={profile} email={currentUser.email} />
          <div className="profile-about-div">
            <TextArea
              name="inputAbout"
              label="About Me"
              value={profile.aboutMe}
            />
          </div>

          <h5>Skills</h5>
          <div className="report-skills-div">{skillsContent}</div>
          <h5>Projects</h5>
          <div className="report-projects-div">{projectsContent}</div>
        </div>
      </PDFExport>
    </ReportContainer>
  );
}

export default Report;
