import React, { useState } from "react";
import { TextArea } from "../../components/TextArea";
import { TextInput } from "../../components/TextInput";
import { PrimaryButton } from "../../components/PrimaryButton";
import "./Profile.css";

import { useSelector } from "react-redux";
import {useNavigate} from "react-router-dom";

function Profile(props) {
  const initialState = {
    inputContact: "",
    inputAbout: "",
  };
  const [state, setState] = useState(initialState);

  // Handle input change for each input
  function handleChange(e) {
    const value = e.target.value;
    setState({
      ...state,
      [e.target.name]: value,
    });
  }
  return (
    <div className="profile">
      <header className="profile-header">
        <span className="avatar">JR</span>
        <h3 className="profile-name-location">
          <span className="profile-name">Joe Root</span>
          <span className="profile-location">San Francisco, CA</span>
        </h3>
      </header>
      <form className="profile-main">
        <TextInput
          type="email"
          label="Email address"
          name="emailInput"
          value=""
          placeholder="Enter your email here"
          onChange={() => {}}
        />
        <TextInput
          type="text"
          label="Contact"
          name="inputContact"
          value={state.inputContact}
          placeholder="Enter your contact here"
          onChange={handleChange}
        />
        <TextArea
          label="About Me"
          name="inputAbout"
          value={state.inputAbout}
          placeholder="Tell us about yourself"
          onChange={handleChange}
        />

        <PrimaryButton onClick={() => {}}> Save </PrimaryButton>
      </form>
    </div>
  );
}

export default Profile;
